function fireOnFullMinute() {
    const now = new Date();
    const msTillNextMinute = 60000 - (now.getSeconds() * 1000 + now.getMilliseconds());

    setTimeout(() => {
        node.send({ payload: new Date() });
        fireOnFullMinute(); // wiederholen
    }, msTillNextMinute);
}

fireOnFullMinute();
return null; // weil wir per setTimeout senden
